insert into products (category_id,product_name,price,quantity_remaining,description,image) values (1, '1 Oz. Beard Oil', 11.99, 18, 'Handmade with 100% natural oils, this beard oil absorbs quickly and gives your beard a smooth, soft, subtle shine. Great for conditioning the skin underneath  your beard. Promotes hair growth by helping you maintain a healthy beard.','img/oil.jpg');
